if (msg.msg433.TEMP!==undefined) {
    if (parseInt(msg.msg433.TEMP, 16) >= 0x8000) {
        msg.msg433.TEMP = (parseInt(msg.msg433.TEMP, 16) & 0x7FFF) / 10 * -1;
    } else {
        msg.msg433.TEMP = parseInt(msg.msg433.TEMP, 16) / 10;
    }
} else msg.msg433.TEMP=-999.0;
node.status({fill:"blue",shape:"ring",text: msg.msg433.TEMP });
return msg;

if (msg.msg433.HUM!==undefined) {
    msg.msg433.HUM = parseInt(msg.msg433.HUM, 10);
}
else msg.msg433.HUM=-999.0;
node.status({fill:"blue",shape:"ring",text: msg.msg433.HUM });
return msg;

var global_name = "rainstat";   // Name of the global variable

var rainstat = global.get(global_name);

 msg.msg433.RAINRATE = -999;

if (msg.msg433.RAIN!==undefined) {
    msg.msg433.RAIN = parseInt(msg.msg433.RAIN, 16) /10;
    msg.msg433.RAINRATE = msg.msg433.RAIN - rainstat;
    global.set(global_name,msg.msg433.RAIN);
} else msg.msg433.RAIN=-999.0;
node.status({fill:"blue",shape:"ring",text: rainstat });

return msg;

if (msg.msg433.WINSP!==undefined) {
    msg.msg433.WINSP = parseInt(msg.msg433.WINSP, 16) /10;
} else msg.msg433.WINSP=0;
node.status({fill:"blue",shape:"ring",text: msg.msg433.WINSP });
return msg;

if (msg.msg433.WINGS!==undefined) {
    msg.msg433.WINGS  = parseInt(msg.msg433.WINGS , 16)/10;
}
else msg.msg433.WINGS=0;
node.status({fill:"blue",shape:"ring",text: msg.msg433.WINGS });
